INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`)
VALUES
	(1,1,'2015-07-23 18:49:41','2015-07-23 18:49:41','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','open','open','','hello-world','','','2015-07-23 18:49:41','2015-07-23 18:49:41','',0,'http://dev.post.local/?p=1',0,'post','',1),
	(2,1,'2015-07-23 18:49:41','2015-07-23 18:49:41','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://dev.post.local/wp/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','publish','open','open','','sample-page','','','2015-07-23 18:49:41','2015-07-23 18:49:41','',0,'http://dev.post.local/?page_id=2',0,'page','',0),
	(3,1,'2015-07-23 18:49:48','0000-00-00 00:00:00','','Auto Draft','','auto-draft','open','open','','','','','2015-07-23 18:49:48','0000-00-00 00:00:00','',0,'http://dev.post.local/?p=3',0,'post','',0),
	(4,1,'2015-07-23 19:53:47','2015-07-23 19:53:47','<p>Your Name (required)<br />\n    [text* your-name] </p>\n\n<p>Your Email (required)<br />\n    [email* your-email] </p>\n\n<p>Subject<br />\n    [text your-subject] </p>\n\n<p>Your Message<br />\n    [textarea your-message] </p>\n\n<p>[submit \"Send\"]</p>\n[your-subject]\n[your-name] <wordpress@dev.post.local>\nFrom: [your-name] <[your-email]>\nSubject: [your-subject]\n\nMessage Body:\n[your-message]\n\n--\nThis e-mail was sent from a contact form on POST (http://dev.post.local)\ndamon@elefintdesigns.com\nReply-To: [your-email]\n\n0\n0\n\n[your-subject]\nPOST <wordpress@dev.post.local>\nMessage Body:\n[your-message]\n\n--\nThis e-mail was sent from a contact form on POST (http://dev.post.local)\n[your-email]\nReply-To: damon@elefintdesigns.com\n\n0\n0\nYour message was sent successfully. Thanks.\nFailed to send your message. Please try later or contact the administrator by another method.\nValidation errors occurred. Please confirm the fields and submit it again.\nFailed to send your message. Please try later or contact the administrator by another method.\nPlease accept the terms to proceed.\nPlease fill in the required field.\nThis input is too long.\nThis input is too short.','Contact form 1','','publish','open','open','','contact-form-1','','','2015-07-23 19:53:47','2015-07-23 19:53:47','',0,'http://dev.post.local/?post_type=wpcf7_contact_form&p=4',0,'wpcf7_contact_form','',0);

